import React, { useEffect, useState } from "react";
import { signOut, onAuthStateChanged } from "firebase/auth";
import { auth, db } from "../firebase.js"
import { useNavigate } from "react-router-dom";
import { uid } from "uid";
import { set,ref, onValue, remove, update } from "firebase/database";
import './homepage.css';
import logo from '../images/checklist.png';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckIcon from '@mui/icons-material/Check';
import LogoutIcon from '@mui/icons-material/Logout';

export default function Homepage() {
    const [todo, setTodo] = useState("");
    const [todoList, setTodoList] = useState([]);
    const [isEdit, setEdit] = useState(false);
    const [tempUid, setTempUid] = useState("");
    const nav = useNavigate();

    useEffect(() => {
        auth.onAuthStateChanged((user) => {
          if (user) {
            // read
            onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
              setTodoList([]);
              const data = snapshot.val();
              if (data !== null) {
                Object.values(data).map((todo) => {
                  setTodoList((oldArray) => [...oldArray, todo]);
                });
              }
            });
          } else if (!user) {
            nav("/");
          }
        });
      }, []);

    const handleSignOut = () => {
        signOut(auth)
            .then(() => {
                nav("/");
            })
            .catch(err => {
                    alert(err.message);
                }
            );
    }

    // Create
    const addToDb = () => {
        const userId = uid();
        set(ref(db,`/${auth.currentUser.uid}/${userId}`), {
            todo: todo,
            userId: userId,
        });
        setTodo("");
    };
    // Read

    // Update
    const editListItem = (todo) => {
        setEdit(true);
        setTodo(todo.todo);
        setTempUid(todo.userId);
    };

    const handleConfirm = () => {
        update(ref(db,`/${auth.currentUser.uid}/${tempUid}`), {
            todo: todo, 
            tempUid: tempUid
        });

        setTodo("");
        setEdit(false);
    };
    // Delete
    const deleteListItem = (uid) => {
        remove(ref(db,`/${auth.currentUser.uid}/${uid}`));
    };

    const changeEditIcon = () => {
        setEdit(true);
    }

    return (
        <div className="homepage">
            <h1 className="add-heading"><img id="logoImg" src={ logo }></img>Todo List</h1>
            <input className="add-todo-input" type="text" placeholder="Add Todo Item" value={todo} onFocus={() => changeEditIcon()} onChange={(e) => setTodo(e.target.value)}></input>
            {todoList.map((todo) => (
                <div className="todo-items">
                    <p className="items">{todo.todo}</p>
                    <EditIcon className="edit-btn" onClick={() => editListItem(todo)}>Edit</EditIcon>
                    <DeleteIcon className="trash-btn" onClick={() => deleteListItem(todo.userId)}>Delete</DeleteIcon>
                </div>
            ))}
            {isEdit ? (
                <div>
                    <CheckIcon className="add-icon" onClick={ handleConfirm }>Confirm</CheckIcon>
                </div>
            ) : (
                <div>
                    <AddIcon onClick={ addToDb } className="add-icon"></AddIcon>
                </div>
            )}
            <LogoutIcon className="logout-icon" onClick={ handleSignOut }>Sign Out</LogoutIcon>
        </div>
    )
}
