import React, { useEffect, useState } from "react";
import { signOut, onAuthStateChanged } from "firebase/auth";
import { auth, db } from "../firebase.js"
import { useNavigate } from "react-router-dom";
import { uid } from "uid";
import { set,ref, onValue, remove, update } from "firebase/database";
import './homepage.css';
import logo from '../images/checklist.png';
import AddIcon from '@mui/icons-material/Add';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckIcon from '@mui/icons-material/Check';
import LogoutIcon from '@mui/icons-material/Logout';

export default function Homepage() {
    const [inputTodo, setInputTodo] = useState(""); // Separate state for input text
    const [todoList, setTodoList] = useState([]);
    const [isEdit, setEdit] = useState(false);
    const [tempUid, setTempUid] = useState("");
    const [editTodoId, setEditTodoId] = useState(""); // Track which todo item is being edited
    const nav = useNavigate();

    useEffect(() => {
        auth.onAuthStateChanged((user) => {
          if (user) {
            // read
            onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
              setTodoList([]);
              const data = snapshot.val();
              if (data !== null) {
                Object.values(data).map((todo) => {
                  setTodoList((oldArray) => [...oldArray, todo]);
                });
              }
            });
          } else if (!user) {
            nav("/");
          }
        });
      }, []);

    const handleSignOut = () => {
        signOut(auth)
            .then(() => {
                nav("/");
            })
            .catch(err => {
                    alert(err.message);
                }
            );
    }

    const addToDb = () => {
        const userId = uid();
        set(ref(db,`/${auth.currentUser.uid}/${userId}`), {
            todo: inputTodo, // Use inputTodo instead of todo
            userId: userId,
        });
        setInputTodo(""); // Reset inputTodo
    };

    const editListItem = (todo) => {
        setEdit(true);
        setEditTodoId(todo.userId); // Set the ID of the todo item being edited
        setInputTodo(todo.todo); // Use setInputTodo to set the input text
        setTempUid(todo.userId);
    };

    const handleConfirm = () => {
        update(ref(db,`/${auth.currentUser.uid}/${tempUid}`), {
            todo: inputTodo, // Use inputTodo instead of todo
            tempUid: tempUid
        });

        setInputTodo(""); // Reset inputTodo
        setEdit(false);
        setEditTodoId(""); // Reset edit state
    };

    const deleteListItem = (uid) => {
        remove(ref(db,`/${auth.currentUser.uid}/${uid}`));
        setTodoList(todoList.filter(todo => todo.userId !== uid)); // Remove deleted item from todoList
    };

    const changeEditIcon = () => {
        if (!isEdit) { // Only add item if not in edit mode
            addToDb();
        }
        setEdit(true);
    };

    return (
        <div className="homepage">
            <h1 className="add-heading"><img id="logoImg" src={ logo } alt="Logo" />Todo List</h1>
            <input className="add-todo-input" type="text" placeholder="Add Todo Item" value={inputTodo} onChange={(e) => setInputTodo(e.target.value)} />
            <AddIcon onClick={ changeEditIcon } className="add-icon"></AddIcon> {/* Use the plus icon to control adding */}
            {todoList.map((todoItem) => (
                <div className="todo-items" key={todoItem.userId}>
                    {editTodoId === todoItem.userId ? (
                        <input
                            type="text"
                            className="edit-todo-input"
                            value={inputTodo} // Use inputTodo instead of todo
                            onChange={(e) => setInputTodo(e.target.value)}
                        />
                    ) : (
                        <p className="items">{todoItem.todo}</p>
                    )}
                    <EditIcon
                        className="edit-btn"
                        onClick={() => editListItem(todoItem)}
                    >
                        Edit
                    </EditIcon>
                    <DeleteIcon
                        className="trash-btn"
                        onClick={() => deleteListItem(todoItem.userId)}
                    >
                        Delete
                    </DeleteIcon>
                </div>
            ))}
            {isEdit ? (
                <div>
                    <CheckIcon className="add-icon" onClick={ handleConfirm }>Confirm</CheckIcon>
                </div>
            ) : null}
            <LogoutIcon className="logout-icon" onClick={ handleSignOut }>Sign Out</LogoutIcon>
        </div>
    );
}
