import React, { useEffect, useState } from 'react';
import { signInWithEmailAndPassword, onAuthStateChanged, createUserWithEmailAndPassword } from 'firebase/auth';
import { auth } from '../firebase.js';
import { useNavigate } from 'react-router-dom';

export default function Welcome() {
    const [email,setEmail] = useState('');
    const [pass,setPass] = useState('');
    const [isSigning, setIsSigning] = useState(false);
    const [signUpInfo, setSignUpInfo] = useState({
        email: "",
        confirmEmail: "",
        pass: "",
        confirmPass: ""
    });
    const nav = useNavigate();

    useEffect(() => {
        auth.onAuthStateChanged((user) => {
            if (user) {
                nav("/homepage");
            }
        })
    });

    const emailChange = (e) => {
        setEmail(e.target.value);
    };

    const passChange = (e) => {
        setPass(e.target.value);
    };
    const signIn = () => {
        signInWithEmailAndPassword(auth,email,pass)
            .then(() => {
                nav('/homepage')
            })
            .catch(err => 
                alert(err.message)
            );
    };

    const signUp = () => {
        if (signUpInfo.email !== signUpInfo.confirmEmail || signUpInfo.password !== signUpInfo.confirmPassword) {}
        createUserWithEmailAndPassword(auth, email, pass)
            .then(() => {
                nav('/homepage')
            })
            .catch(err => 
                alert(err.message)
            );
    };

    return (
        <div className="welcome">
            <h1>Todo List</h1>
            <div className='login-signin-container'>
                {isSigning ? (
                    <>
                    <input type="email" placeholder="Email" value={signUpInfo.email} onChange={(e) => setSignUpInfo({...signUpInfo, email: e.target.value})}></input>
                    <input type="email" placeholder="Confirm Email" value={signUpInfo.confirmEmail} onChange={(e) => setSignUpInfo({...signUpInfo, confirmEmail: e.target.value})}></input>
                    <input type="password" placeholder="Password" value={signUpInfo.pass} onChange={(e) => setSignUpInfo({...signUpInfo, pass: e.target.value})}></input>
                    <input type="password" placeholder="Confirm Password" value={signUpInfo.confirmPass} onChange={(e) => setSignUpInfo({...signUpInfo, confirmPass: e.target.value})}></input>
                    <button onClick={signUp}>Sign Up</button>
                    <button onClick={() => setIsSigning(false)}>Go Back</button>
                </>
                ) : (
                    <>
                        <input type="email" placeholder="Email" onChange={emailChange} value={email}></input>
                        <input type="password" placeholder="Password" onChange={passChange} value={pass}></input>
                        <button onClick={signIn}>Log In</button>
                        <button onClick={() => setIsSigning(true)}>Create an Account</button>
                    </>
                )}
            </div>
        </div>
    )
}
