export default function Homepage() {
    const [todoList, setTodoList] = useState([]);
    const [isEditing, setIsEditing] = useState(false);
    const [tempUid, setTempUid] = useState("");
    const [newTodo, setNewTodo] = useState("");
    const nav = useNavigate();

    useEffect(() => {
        const unsubscribe = onAuthStateChanged(auth, (user) => {
            if (user) {
                onValue(ref(db, `/${auth.currentUser.uid}`), (snapshot) => {
                    const data = snapshot.val();
                    if (data !== null) {
                        const todoItems = Object.values(data);
                        setTodoList(todoItems);
                    } else {
                        setTodoList([]);
                    }
                });
            } else {
                nav("/");
            }
        });
        return () => unsubscribe();
    }, [nav]);

    const handleSignOut = () => {
        signOut(auth)
            .then(() => {
                nav("/");
            })
            .catch(err => {
                alert(err.message);
            });
    };

    const addNewItem = () => {
        setIsEditing(true);
    };

    const confirmEdit = () => {
        update(ref(db, `/${auth.currentUser.uid}/${tempUid}`), {
            todo: newTodo
        }).then(() => {
            setTempUid("");
            setNewTodo("");
            setIsEditing(false);
        }).catch((error) => {
            console.error("Error updating document: ", error);
        });
    };

    const cancelEdit = () => {
        setTempUid("");
        setNewTodo("");
        setIsEditing(false);
    };

    const editListItem = (todo) => {
        setTempUid(todo.userId);
        setNewTodo(todo.todo);
        setIsEditing(true);
    };

    const deleteListItem = (uid) => {
        remove(ref(db, `/${auth.currentUser.uid}/${uid}`)).catch((error) => {
            console.error("Error removing document: ", error);
        });
    };

    const handleAdd = () => {
        const userId = uid();
        set(ref(db, `/${auth.currentUser.uid}/${userId}`), {
            todo: newTodo,
            userId: userId
        }).then(() => {
            setNewTodo("");
            setIsEditing(false);
        }).catch((error) => {
            console.error("Error adding document: ", error);
        });
    };

    return (
        <div className="homepage">
            <h1 className="add-heading"><img id="logoImg" src={logo} alt="Logo" />Todo List</h1>
            {todoList.map((todo) => (
                <div className="todo-items" key={todo.userId}>
                    {isEditing && tempUid === todo.userId ? (
                        <div>
                            <input
                                type="text"
                                value={newTodo}
                                onChange={(e) => setNewTodo(e.target.value)}
                            />
                            <CheckIcon className="add-icon" onClick={confirmEdit}>Confirm</CheckIcon>
                            <DeleteIcon className="trash-btn" onClick={() => deleteListItem(todo.userId)}>Delete</DeleteIcon>
                        </div>
                    ) : (
                        <>
                            <p className="items">{todo.todo}</p>
                            <EditIcon className="edit-btn" onClick={() => editListItem(todo)}>Edit</EditIcon>
                            <DeleteIcon className="trash-btn" onClick={() => deleteListItem(todo.userId)}>Delete</DeleteIcon>
                        </>
                    )}
                </div>
            ))}
            {!isEditing && (
                <AddIcon onClick={addNewItem} className="add-icon"></AddIcon>
            )}
            <LogoutIcon className="logout-icon" onClick={handleSignOut}>Sign Out</LogoutIcon>
        </div>
    );
}
